import React, {useState} from 'react';

import { InputText } from 'primereact/inputtext';
import {Button} from "primereact/button";


function LoginForm({Login,error}){
    const[details,setDetails]= useState({usernameOrEmail:"",password:""});
    const submitHandler = e =>{
        e.preventDefault();
        Login(details);
    }

    return (
        <form onSubmit={submitHandler}>
            <div className="flex align-items-center py-8 justify-content-center">
                <div className="surface-card p-4 shadow-2 border-round w-full lg:w-3">
                    <div className="text-center ">
                        <img src="assets/layout/images/logopge.png" alt="hyper" height={150} className="mb-3" />

                    </div>
                    <span class="text-center text-red font-medium line-height-3">{error}</span>
            <div>
                    <label htmlFor="email" className="block text-900 font-medium mb-2">Usuario</label>
                    <InputText type="text" name="usernameOrEmail" id="usernameOrEmail" className="w-full mb-3" onChange={e => setDetails({
                        ...details, usernameOrEmail: e.target.value
                    })} value={details.usernameOrEmail}
                    />
                <label htmlFor="password" className="block text-900 font-medium mb-2">Password</label>
                    <InputText type="password" name="password" id="password" className="w-full mb-3"
                           onChange={e => setDetails({
                               ...details, password: e.target.value
                           })} value={details.password}
                    />
                <Button label="INGRESAR" icon="pi pi-lock" className="p-button-danger w-full" />
            </div>
                </div>
            </div>
        </form>
    )
}
export default LoginForm
