import React, {useState} from 'react';

function LoginForm({Login,error}){
    const[details,setDetails]= useState({usernameOrEmail:"",password:""});
    const submitHandler = e =>{
        e.preventDefault();
        Login(details);
    }

    return (
        <form onSubmit={submitHandler}>
            <div className="flex align-items-center justify-content-center">
                <div className="surface-card p-4 shadow-2 border-round w-full lg:w-6">
                    <div className="text-center mb-5">
                        <img src="assets/images/blocks/logos/hyper.svg" alt="hyper" height={50} className="mb-3" />
                        <div className="text-900 text-3xl font-medium mb-3">Welcome Back</div>
                        <span className="text-600 font-medium line-height-3">Don't have an account?</span>
                        <a className="font-medium no-underline ml-2 text-blue-500 cursor-pointer">Create today!</a>
                    </div>
            <div className="form-inner">
               

                <div className="form-group">
                    <label htmlFor="email">Usuario:</label>
                    <input type="text" name="usernameOrEmail" id="usernameOrEmail" onChange={e => setDetails({
                        ...details, usernameOrEmail: e.target.value
                    })} value={details.usernameOrEmail}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password:</label>
                    <input type="password" name="password" id="password"
                           onChange={e => setDetails({
                               ...details, password: e.target.value
                           })} value={details.password}
                    />
                </div>
                <input type="submit" value="Login"/>
            </div>
                </div>
            </div>
        </form>
    )
}
export default LoginForm
