import React, { useState, useEffect, useRef } from 'react';
import classNames from 'classnames';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Toast } from 'primereact/toast';
import { Button } from 'primereact/button';
import { FileUpload } from 'primereact/fileupload';
import { Rating } from 'primereact/rating';
import { Toolbar } from 'primereact/toolbar';
import { InputTextarea } from 'primereact/inputtextarea';
import { RadioButton } from 'primereact/radiobutton';
import { InputNumber } from 'primereact/inputnumber';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import { ProductService } from '../service/ProductService';
import {InputSwitch} from "primereact/inputswitch";
import {Dropdown} from "primereact/dropdown";
import {UsuarioService} from "../service/UsuarioService";
import {AplicacionService} from "../service/AplicacionService";

const Menu = () => {

    let emptyUsuario = {
        id: null,
        aplicacion:'',
        rol:'',
        name: '',
        usuario: null,
        password: null,
        tipoUser: 0,
        id_estado: 1,
        fec_cre: 0,
        fec_mod: 0,
        usu_cre: '',
        usu_mod: ''
    };

    const [usuarios, setUsuarios] = useState(null);
    const [productDialog, setProductDialog] = useState(false);
    const [deleteProductDialog, setDeleteProductDialog] = useState(false);
    const [deleteProductsDialog, setDeleteProductsDialog] = useState(false);
    const [usuario, setUsuario] = useState(emptyUsuario);
    const [selectedProducts, setSelectedProducts] = useState(null);
    const [submitted, setSubmitted] = useState(false);
    const [globalFilter, setGlobalFilter] = useState(null);
    const toast = useRef(null);
    const dt = useRef(null);
    const[state,setState]= useState({checked1:false});
    const[aplicacion,setAplicacion] = useState(null);
    const[apli, setApli] = useState({
        citySelectItems: [
            { label: 'ABOGADO', value: 'ABOGADO' },
            { label: 'ADM_PROC', value: 'ADM_PROC' },

        ],
        city: ''
    });
    useEffect(() => {

        const usuarioService = new UsuarioService();
        usuarioService.getUsuarios().then(data => setUsuarios(data));
    }, []);
    const aplicacionService = new AplicacionService();
    aplicacionService.getAplicacion().then(data => setAplicacion(data));
    const formatCurrency = (value) => {
        return value.toLocaleString('en-US', { style: 'currency', currency: 'USD' });
    }

    const openNew = () => {
        // setProduct(emptyProduct);
        setUsuario(emptyUsuario);
        setSubmitted(false);
        setProductDialog(true);
    }

    const hideDialog = () => {
        setSubmitted(false);
        setProductDialog(false);
    }

    const hideDeleteProductDialog = () => {
        setDeleteProductDialog(false);
    }

    const hideDeleteProductsDialog = () => {
        setDeleteProductsDialog(false);
    }

    const saveProduct = () => {
        setSubmitted(true);

        if (usuario.name.trim()) {
            let _usuarios = [...usuarios];
            let _usuario = { ...usuario };
            if (usuario.id) {
                const index = findIndexById(usuario.id);

                _usuarios[index] = _usuario;
                toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Product Updated', life: 3000 });
            }
            else {
                _usuario.id = createId();
                _usuario.rol ="Abogado"
                _usuario.aplicacion ="SAEP"
                _usuario.name = usuario.name
                _usuario.usuario = usuario.usuario
                _usuario.password = usuario.password
                _usuario.id_estado = "1"
                _usuarios.push(_usuario);
                toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Product Created', life: 3000 });
            }

             setUsuarios(_usuarios);
            setProductDialog(false);
            setUsuario(emptyUsuario);
        }
    }

    const editProduct = (rol) => {
        //setProduct({ ...product });
        setUsuario({ ...rol });
        setProductDialog(true);
    }

    const confirmDeleteProduct = (usuario) => {
        // setProduct(product);
        setUsuario(usuario);
        setDeleteProductDialog(true);
    }

    const deleteProduct = () => {
        let _usuarios = usuarios.filter(val => val.id !== usuario.id);
        setUsuarios(_usuarios);
        setDeleteProductDialog(false);
        //   setProduct(emptyProduct);
        setUsuario(emptyUsuario);
        toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Product Deleted', life: 3000 });
    }

    const findIndexById = (id) => {
        let index = -1;
        for (let i = 0; i < usuarios.length; i++) {
            if (usuarios[i].id === id) {
                index = i;
                break;
            }
        }

        return index;
    }

    const createId = () => {
        let id = '';
        let chars = '0123456789';
        for (let i = 0; i < 5; i++) {
            id += chars.charAt(Math.floor(Math.random() * chars.length));
        }
        return id;
    }

    const exportCSV = () => {
        dt.current.exportCSV();
    }

    const confirmDeleteSelected = () => {
        setDeleteProductsDialog(true);
    }

    const deleteSelectedProducts = () => {
        let _usuarios = usuarios.filter(val => !selectedProducts.includes(val));
        setUsuarios(_usuarios);
        setDeleteProductsDialog(false);
        setSelectedProducts(null);
        toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Products Deleted', life: 3000 });
    }
    const onAplicacionChange = (e) =>{

     //   setSelectedAplicacion(e.value)
        let _usuario = { ...usuario };
        _usuario[`${'id_aplicacion'}`] = e.value;

        setUsuario(_usuario);
        console.log(_usuario);
    }
    const onRolChange = (e) =>{


        let _rol = { ...rol };
        _rol[`${'id_aplicacion'}`] = e.value;

        setUsuario(_rol);
        console.log(_rol);
    }
    const onCategoryChange = (e) => {
        let _usuario = { ...usuario };
        _usuario['category'] = e.value;
        //   setProduct(_product);
        setUsuario(_usuario);
    }

    const onInputChange = (e, name) => {
        const val = (e.target && e.target.value) || '';
        let _usuario = { ...usuario };
        _usuario[`${name}`] = val;

        setUsuario(_usuario);
    }

    const onInputNumberChange = (e, name) => {
        const val = e.value || 0;
        let _usuario = { ...usuario };
        _usuario[`${name}`] = val;

        setUsuario(_usuario);
    }

    const leftToolbarTemplate = () => {
        return (
            <React.Fragment>
                <div className="my-2">
                    <Button label="Nuevo" icon="pi pi-plus" className="p-button-danger mr-2" onClick={openNew} />

                </div>
            </React.Fragment>
        )
    }

    const rightToolbarTemplate = () => {
        return (
            <React.Fragment>

            </React.Fragment>
        )
    }
    const categoryBodyTemplate = (rowData) => {
        return (
            <>
                <span className="p-column-title">Category</span>
                {rowData.category}
            </>
        );
    }
   /* const codeBodyTemplate = (rowData) => {
        return (
            <>
                <span className="p-column-title">Code</span>
                {rowData.code}
            </>
        );
    }
*/
    const idBodyTemplate = (rowData) => {
        return (
            <>
                <span className="p-column-title">Id</span>
                {rowData.id}
            </>
        );
    }
    const nameBodyTemplate = (rowData) => {
        return (
            <>
                <span className="p-column-title">Name</span>
                {rowData.name}
            </>
        );
    }

    const usuarioBodyTemplate = (rowData) => {
        return (
            <>
                <span className="p-column-title">Usuario</span>
                {rowData.usuario}
            </>
        );
    }

    const rolBodyTemplate = (rowData) => {
        return (
            <>
                <span className="p-column-title">Rol</span>
                {rowData.rol}
            </>
        );
    }
    const aplicacionBodyTemplate = (rowData) => {
        return (
            <>
                <span className="p-column-title">Rol</span>
                {rowData.aplicacion}
            </>
        );
    }
    /*  const tipoUsuarioBodyTemplate = (rowData) => {
         return (
             <>
                 <span className="p-column-title">Rol</span>
                 {rowData.tipoUser}
             </>
         );
     }
      const ratingBodyTemplate = (rowData) => {
           return (
               <>
                   <span className="p-column-title">Reviews</span>
                   <Rating value={rowData.rating} readonly cancel={false} />
               </>
           );
       }
   */
    const statusBodyTemplate = (rowData) => {
        return (
            <>
                <span className="p-column-title">Status</span>
                <span className={`product-badge`}>{rowData.id_estado==1?"Activo":"Inactivo"}</span>
            </>
        )
    }

    const actionBodyTemplate = (rowData) => {
        return (
            <div className="actions">
                <Button icon="pi pi-pencil" className="p-button-rounded p-button-danger mr-2" onClick={() => editProduct(rowData)} />
                <Button icon="pi pi-trash" className="p-button-rounded p-button-warning mt-2" onClick={() => confirmDeleteProduct(rowData)} />
            </div>
        );
    }

    const header = (
        <div className="flex flex-column md:flex-row md:justify-content-between md:align-items-center">
            <h5 className="m-0">Usuario</h5>
            <span className="block mt-2 md:mt-0 p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    const productDialogFooter = (
        <>
            <Button label="Cancelar" icon="pi pi-times" className="p-button-text" onClick={hideDialog} />
            <Button label="Guardar" icon="pi pi-check" className="p-button-text" onClick={saveProduct} />
        </>
    );
    const deleteProductDialogFooter = (
        <>
            <Button label="No" icon="pi pi-times" className="p-button-text" onClick={hideDeleteProductDialog} />
            <Button label="Si" icon="pi pi-check" className="p-button-text" onClick={deleteProduct} />
        </>
    );
    const deleteProductsDialogFooter = (
        <>
            <Button label="No" icon="pi pi-times" className="p-button-text" onClick={hideDeleteProductsDialog} />
            <Button label="Si" icon="pi pi-check" className="p-button-text" onClick={deleteSelectedProducts} />
        </>
    );

    return (
        <div className="grid crud-demo">
            <div className="col-12">
                <div className="card">
                    <Toast ref={toast} />
                    <Toolbar className="mb-4" left={leftToolbarTemplate} right={rightToolbarTemplate}></Toolbar>

                    <DataTable ref={dt} value={usuarios} selection={selectedProducts} onSelectionChange={(e) => setSelectedProducts(e.value)}
                               dataKey="id" paginator rows={10} rowsPerPageOptions={[5, 10, 25]} className="datatable-responsive"
                               paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                               currentPageReportTemplate="Showing {first} to {last} of {totalRecords} products"
                               globalFilter={globalFilter} emptyMessage="No products found." header={header} responsiveLayout="scroll">

                        <Column field="Id" header="Id" sortable body={idBodyTemplate} headerStyle={{ width: '5%', minWidth: '10rem' }}></Column>
                        <Column field="Nombre" header="Nombre" sortable body={nameBodyTemplate} headerStyle={{ width: '20%', minWidth: '10rem' }}></Column>

                        <Column field="Usuario" header="Usuario" sortable body={usuarioBodyTemplate} headerStyle={{ width: '10%', minWidth: '10rem' }}></Column>
                        <Column field="Rol" header="Rol" sortable body={rolBodyTemplate} headerStyle={{ width: '10%', minWidth: '10rem' }}></Column>
                        <Column field="Rol" header="Aplicacion" sortable body={aplicacionBodyTemplate} headerStyle={{ width: '10%', minWidth: '10rem' }}></Column>

                        <Column field="inventoryStatus" header="Estado" body={statusBodyTemplate} sortable headerStyle={{ width: '10%', minWidth: '10rem' }}></Column>
                        <Column body={actionBodyTemplate}></Column>
                    </DataTable>

                    <Dialog visible={productDialog} style={{ width: '450px' }} header="Usuario" modal className="p-fluid" footer={productDialogFooter} onHide={hideDialog}>
                        <div className="field">
                            <label htmlFor="name">Aplicación</label>
                            <Dropdown
                                style={{ width: 405 }}
                                value={usuario.id_aplicacion}
                                options={aplicacion}
                                onChange={e =>
                                    onAplicacionChange(e)

                                }
                                optionValue="nombre"
                                optionLabel="nombre"
                                placeholder='Seleccione aplicación'
                            />
                        </div>
                        <div className="field">
                            <label htmlFor="name">Rol</label>
                            <Dropdown
                                style={{ width: 405 }}
                                value={usuario.rol}
                                options={rols}
                                onChange={e =>
                                    onAplicacionChange(e)

                                }
                                optionValue="nombre"
                                optionLabel="nombre"
                                placeholder='Seleccione aplicación'
                            />
                        </div>
                        <div className="field">
                            <label htmlFor="name">Nombre</label>
                            <InputText id="name" value={usuario.name} onChange={(e) => onInputChange(e, 'name')} required autoFocus className={classNames({ 'p-invalid': submitted && !usuario.name })} />
                            {submitted && !usuario.name && <small className="p-invalid">Name is required.</small>}
                        </div>
                        <div className="field">
                            <label htmlFor="name">Usuario</label>
                            <InputText id="name" value={usuario.usuario} onChange={(e) => onInputChange(e, 'usuario')} required autoFocus className={classNames({ 'p-invalid': submitted && !usuario.usuario })} />
                            {submitted && !usuario.usuario && <small className="p-invalid">Name is required.</small>}
                        </div>
                        <div className="field">
                            <label htmlFor="name">Password</label>
                            <InputText id="name" type="password" value={usuario.password} onChange={(e) => onInputChange(e, 'password')} required autoFocus className={classNames({ 'p-invalid': submitted && !usuario.password })} />
                            {submitted && !usuario.password && <small className="p-invalid">Name is required.</small>}
                        </div>
                        <div className="field">
                            <label htmlFor="name">Tipo Usuario</label>
                            <Dropdown
                                style={{ width: 405 }}
                                value={apli.city}
                                options={apli.citySelectItems}
                                onChange={e => {
                                    setApli({ city: e.value });
                                }}
                                placeholder='Seleccione Tipo Usuario'
                            />
                        </div>
                        <div className="field">
                            <label className="mb-3">Estado</label>
                            <InputSwitch
                                checked={state.checked1}
                                onChange={e => setState({ checked1: e.value })}
                            />
                        </div>

                    </Dialog>

                    <Dialog visible={deleteProductDialog} style={{ width: '450px' }} header="Confirmación" modal footer={deleteProductDialogFooter} onHide={hideDeleteProductDialog}>
                        <div className="flex align-items-center justify-content-center">
                            <i className="pi pi-exclamation-triangle mr-3" style={{ fontSize: '2rem' }} />
                            {usuario && <span> Deseas  eliminar el usuario de <b>{usuario.name}</b>?</span>}
                        </div>
                    </Dialog>

                    <Dialog visible={deleteProductsDialog} style={{ width: '450px' }} header="Confirmación" modal footer={deleteProductsDialogFooter} onHide={hideDeleteProductsDialog}>
                        <div className="flex align-items-center justify-content-center">
                            <i className="pi pi-exclamation-triangle mr-3" style={{ fontSize: '2rem' }} />
                            {usuario && <span>Are you sure you want to delete the selected products?</span>}
                        </div>
                    </Dialog>
                </div>
            </div>
        </div>
    );
}

const comparisonFn = function (prevProps, nextProps) {
    return prevProps.location.pathname === nextProps.location.pathname;
};

export default React.memo(Menu, comparisonFn);
