import React, {useState} from 'react';
import { InputText } from 'primereact/inputtext';
import {Button} from "primereact/button";

function LoginForm({Login,error}){
    const[details,setDetails]= useState({usernameOrEmail:"",password:""});
    const submitHandler = e =>{
        e.preventDefault();
        Login(details);
    }

    return (
        <form onSubmit={submitHandler}>
            <div className="flex align-items-center justify-content-center">
                <div className="surface-card p-4 shadow-2 border-round w-full lg:w-6">
                    <div className="text-center mb-5">
                        <img src="assets/images/blocks/logos/hyper.svg" alt="hyper" height={50} className="mb-3" />
                        <div className="text-900 text-3xl font-medium mb-3">Welcome Back</div>
                        <span className="text-600 font-medium line-height-3">Don't have an account?</span>
                        <a className="font-medium no-underline ml-2 text-blue-500 cursor-pointer">Create today!</a>
                    </div>

                    <div>
                        <label htmlFor="email" className="block text-900 font-medium mb-2">Usuario</label>
                        <InputText name="usernameOrEmail" id="usernameOrEmail"  type="text" className="w-full mb-3" />

                        <label htmlFor="password" className="block text-900 font-medium mb-2">Password</label>
                        <InputText id="password" name="password" type="password" className="w-full mb-3" />

                        <div className="flex align-items-center justify-content-between mb-6">
                            <div className="flex align-items-center">


                            </div>
                            <a className="font-medium no-underline ml-2 text-blue-500 text-right cursor-pointer">Forgot your password?</a>
                        </div>

                        <input type="submit" label="Sign In" icon="pi pi-user" className="w-full" />
                    </div>
                </div>
            </div>
        </form>
    )
}
export default LoginForm
